import React, { useRef } from "react";
import emailjs from "@emailjs/browser";
import Card from "react-bootstrap/Card";
import Form from "react-bootstrap/Form";
import Row from "react-bootstrap/Row";
import Button from "react-bootstrap/Button";

export const ContactForm = () => {
  const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm("service_rj9lbxe", "template_5tdcrwf", form.current, {
        publicKey: "tkNcmbiASi0TUZpR7",
      })
      .then(
        () => {
          console.log("SUCCESS!");
        },
        (error) => {
          console.log("FAILED...", error.text);
        }
      );
  };

  return (
    <Card
      style={{
        maxWidth: "500px",
        backgroundColor: "black",
        border: "solid 3px white",
        borderRadius: "10px",
        height: "398px",
      }}
      className="mx-auto px-4"
    >
      <Form
        onSubmit={sendEmail}
        ref={form}
        style={{ maxWidth: "450px", margin: "50px auto" }}
      >
        <Row>
          <Form.Group>
            <Form.Group.Text>Name</Form.Group.Text>
            <Form.Control type="text" name="user_name" />
          </Form.Group>
        </Row>
        <Row>
          <Form.Group>
            <Form.Group.Text>Email</Form.Group.Text>
            <Form.Control type="email" name="user_email"  />
          </Form.Group>
        </Row>
        <Row>
          <Form.Group>
            <Form.Group.Text>Message</Form.Group.Text>
            <Form.Control type="text" name="message" />
          </Form.Group>
        </Row>
        <label>Name</label>
        <input />
        <label>Email</label>
        <input />
        <label>Message</label>
        <textarea name="message" />
        <input type="submit" value="Send" />
      </Form>
    </Card>
  );
};
