import React, { useRef, useState } from "react";
import emailjs from "@emailjs/browser";
import Card from "react-bootstrap/Card";
import Form from "react-bootstrap/Form";
import Row from "react-bootstrap/Row";
import Button from "react-bootstrap/Button";
import Alert from "react-bootstrap/Alert";

export const ContactForm = () => {
  const [messageSent, setMessageSent] = useState(false);
  const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm("service_rj9lbxe", "template_5tdcrwf", form.current, {
        publicKey: "tkNcmbiASi0TUZpR7",
      })
      .then(
        () => {
          setMessageSent(true);
          console.log("SUCCESS!");
        },
        (error) => {
          console.log("FAILED...", error.text);
        }
      );
  };

  return (
    <Card
      style={{
        maxWidth: "500px",
        backgroundColor: "black",
        border: "solid 3px white",
        borderRadius: "10px",
        height: "398px",
      }}
      className="mx-auto px-4"
    >
      {messageSent ? (
        <Alert variant="success">Message Sent!</Alert>
      ) : (
        <Form
          onSubmit={sendEmail}
          ref={form}
          style={{ maxWidth: "450px", margin: "50px auto" }}
        >
          <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
            <Form.Label >Name</Form.Label>
            <Form.Control type="text" name="name" style={{ width: "95%", height: "28px", borderRadius: '5px'}} />
          </Form.Group>

          <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
            <Form.Label>Email</Form.Label>
            <Form.Control type="email" name="email" style={{ width: "95%" }} />
          </Form.Group>

          <Form.Group className="mb-3" controlId="exampleForm.ControlTextarea1">
            <Form.Label>Message</Form.Label>
            <Form.Control
              type="text"
              name="message"
              as="textarea"
              rows={5}
              style={{ width: "95%" }}
            />
          </Form.Group>

          <Button
            type="submit"
            className="fw-bold"
            style={{
              backgroundColor: "white",
              width: "96%",
              height: "40px",
              color: "purple",
              borderRadius: "7px",
              marginTop: "15px",
            }}
          >
            Send
          </Button>
        </Form>
      )}
    </Card>
  );
};
